CREATE SEQUENCE KISI_ID_SEQ INCREMENT 1 START 1 CACHE 1;
CREATE TABLE KISI
(
    ID								BIGINT 							NOT NULL		DEFAULT NEXTVAL('KISI_ID_SEQ'),
    ISLEM_ZAMAN						TIMESTAMP WITHOUT TIME ZONE		NOT NULL,

    TC_KIMLIK_NO					CHARACTER VARYING(11)			NOT NULL,
    AD								CHARACTER VARYING(25)			NOT NULL,
    SOYAD							CHARACTER VARYING(25)			NOT NULL,

    CONSTRAINT PK_KISI PRIMARY KEY (ID)
);

INSERT INTO KISI(ISLEM_ZAMAN, TC_KIMLIK_NO, AD, SOYAD) VALUES (localtimestamp, '00000000000', 'Kişi Adı', 'Kişi Soyadı');